/*Table structure for table `log_login` */

DROP TABLE IF EXISTS `log_login`;

CREATE TABLE `log_login` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `user_login` varchar(255) DEFAULT NULL COMMENT '登录用户名',
  `ip` varchar(255) DEFAULT NULL COMMENT '登录ip',
  `login_time` int(11) DEFAULT NULL COMMENT '登录时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='登录日志';

/*Data for the table `log_login` */

/*Table structure for table `log_notify` */

DROP TABLE IF EXISTS `log_notify`;

CREATE TABLE `log_notify` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `order_id` varchar(30) DEFAULT NULL,
  `log` text COMMENT '日志记录',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='支付宝通知日志';

/*Data for the table `log_notify` */

/*Table structure for table `log_open` */

DROP TABLE IF EXISTS `log_open`;

CREATE TABLE `log_open` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `uid` int(11) DEFAULT NULL COMMENT '用户id',
  `order_id` varchar(50) DEFAULT NULL COMMENT '订单id',
  `desc` varchar(255) DEFAULT NULL COMMENT '描述',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='业务开通执行主流程日志';

/*Data for the table `log_open` */

/*Table structure for table `log_statistics_by_day` */

DROP TABLE IF EXISTS `log_statistics_by_day`;

CREATE TABLE `log_statistics_by_day` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `session_num` varchar(255) DEFAULT NULL COMMENT '登录终端数统计',
  `count_reg` text COMMENT '注册统计',
  `count_order` text COMMENT '订单统计',
  `count_pay` text COMMENT '付费统计',
  `count_renew` text COMMENT '续费统计',
  `count_new_pay` text COMMENT '新付费统计',
  `create_date` int(11) DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='按照天定时统计';

/*Data for the table `log_statistics_by_day` */

/*Table structure for table `sp_account` */

DROP TABLE IF EXISTS `sp_account`;

CREATE TABLE `sp_account` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL COMMENT '姓名',
  `identify` varchar(255) DEFAULT NULL COMMENT '身份证',
  `type` int(11) DEFAULT NULL COMMENT '账号类型',
  `bank_no` varchar(255) DEFAULT NULL COMMENT '银行账号',
  `is_default` tinyint(3) DEFAULT '0' COMMENT '是否默认',
  `uid` int(11) DEFAULT '0' COMMENT '用户id',
  `account_type` int(11) DEFAULT '0' COMMENT '账号类型',
  `bank_name` varchar(255) DEFAULT NULL COMMENT '所属银行',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='银行账户表';

/*Data for the table `sp_account` */

/*Table structure for table `sp_act_recharge` */

DROP TABLE IF EXISTS `sp_act_recharge`;

CREATE TABLE `sp_act_recharge` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL COMMENT '活动介绍',
  `type` tinyint(4) DEFAULT NULL COMMENT '赠送类型:1余额;2:优惠券:3:两者',
  `condition` decimal(11,2) unsigned DEFAULT NULL COMMENT '赠送条件',
  `bonus` decimal(11,2) unsigned DEFAULT NULL,
  `coupon` int(255) unsigned DEFAULT NULL,
  `state` tinyint(4) DEFAULT '1' COMMENT '-1:删除;0:关闭;1:开启',
  `create_time` int(11) unsigned DEFAULT NULL COMMENT '排序，序号越大越靠前',
  `open_time` int(11) unsigned DEFAULT NULL,
  `close_time` int(11) unsigned DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='充值活动';

/*Data for the table `sp_act_recharge` */

/*Table structure for table `sp_activity` */

DROP TABLE IF EXISTS `sp_activity`;

CREATE TABLE `sp_activity` (
  `id` int(255) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL COMMENT '活动名称',
  `desc` varchar(255) DEFAULT NULL COMMENT '活动介绍',
  `link` tinytext COMMENT '活动链接',
  `src` tinytext COMMENT '图片地址',
  `flag` tinyint(1) DEFAULT '1' COMMENT '1显示，0不显示',
  `sort` int(10) DEFAULT NULL COMMENT '排序，序号越大越靠前',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='活动表';

