/*Table structure for table `sp_compaign` */

DROP TABLE IF EXISTS `sp_compaign`;

CREATE TABLE `sp_compaign` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(100) DEFAULT NULL COMMENT '活动名称',
  `is_nper` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否开启期数限制',
  `nper_limit` int(10) NOT NULL DEFAULT '0' COMMENT '期数限制次数',
  `is_times` tinyint(1) NOT NULL DEFAULT '0' COMMENT '次数限制0关闭1开启',
  `times_limit` int(10) NOT NULL DEFAULT '0' COMMENT '限制次数',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态1正常2删除',
  `type` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '类型1积分',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='活动专区配置表';

