/*Table structure for table `sp_conf` */

DROP TABLE IF EXISTS `sp_conf`;

CREATE TABLE `sp_conf` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `admin_tpl` varchar(255) DEFAULT 'text' COMMENT '后台管理模版|前台请忽略',
  `title` varchar(255) DEFAULT NULL,
  `category` varchar(20) DEFAULT NULL COMMENT '配置分类',
  `name` varchar(255) NOT NULL COMMENT '参数名',
  `editable` enum('false','true') DEFAULT 'true' COMMENT 'false不可删除   true可删除',
  `delable` enum('false','true') DEFAULT 'false' COMMENT 'false不可删除  true可删除',
  `value` text COMMENT '参数值',
  `type` enum('checkbox','radio','select','textarea','img_addr','img_id','input') DEFAULT 'input' COMMENT '配置类型',
  `exec_data` varchar(255) DEFAULT NULL COMMENT '执行数据,用于select的json',
  `remark` varchar(255) DEFAULT NULL COMMENT '参数说明',
  `sort` int(11) DEFAULT '100' COMMENT '排序号',
  `is_must` enum('false','true') DEFAULT 'false' COMMENT '是否必填  true是  false不是',
  `is_default` enum('false','true') DEFAULT 'false' COMMENT '如果为空是否填写默认配置  true是',
  `init_empty` enum('true','false') DEFAULT 'true' COMMENT '初始化的时候为空 true',
  `status` tinyint(2) DEFAULT '1' COMMENT '状态  1正常 -1删除',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=339 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='配置表';

