/*Table structure for table `sp_coupon_cat` */

DROP TABLE IF EXISTS `sp_coupon_cat`;

CREATE TABLE `sp_coupon_cat` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `title` varchar(255) DEFAULT NULL COMMENT '优惠券名称',
  `status` tinyint(11) DEFAULT '1' COMMENT '状态:1生效',
  `bind_category` int(255) DEFAULT NULL COMMENT '可用分类(备用)',
  `discount_type` tinyint(255) DEFAULT '0' COMMENT '折扣类型:0,直接抵现;1,百分比',
  `value` decimal(11,2) DEFAULT '0.00' COMMENT '折扣或折现值',
  `condition` decimal(11,2) DEFAULT NULL COMMENT '使用条件',
  `comment` varchar(255) DEFAULT NULL COMMENT '备注',
  `expire` int(11) DEFAULT NULL COMMENT '过期时间',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `expire_type` tinyint(255) DEFAULT '0' COMMENT '过期类型:0,自获得后exprie的时间内;1,统一设为expire',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='优惠券/红包分类';

