/*Table structure for table `sp_coupon_cat_copy` */

DROP TABLE IF EXISTS `sp_coupon_cat_copy`;

CREATE TABLE `sp_coupon_cat_copy` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `platform` enum('lvdou','jiasutu') DEFAULT NULL COMMENT '优惠券平台',
  `uid` int(11) DEFAULT NULL COMMENT '创建人id',
  `title` varchar(255) DEFAULT NULL COMMENT '优惠券名称',
  `status` int(11) DEFAULT '1' COMMENT '状态:1生效',
  `bind_pak` varchar(255) DEFAULT '' COMMENT '绑定套餐id,用逗号分割:9,10  套餐为9或10的时候可用,当优惠券类型为时间充值券的时候,本字段表示充值用户套餐',
  `limit_time` varchar(255) DEFAULT '' COMMENT '限定开通时长(月): 格式:12-24,1-3',
  `limit_session` int(5) DEFAULT NULL COMMENT '终端数限制',
  `type` enum('add_time','recharge','reduce_money','discount') DEFAULT NULL COMMENT '''add_time''折扣,''recharge''减现金,''reduce_money''充值金额,''discount''充值时间',
  `value` decimal(10,1) DEFAULT '0.0' COMMENT '折扣或折现值',
  `start_time` int(11) DEFAULT '0' COMMENT '生效时间',
