DROP TABLE IF EXISTS `sp_nper_list`;

CREATE TABLE `sp_nper_list` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `pid` int(11) DEFAULT NULL COMMENT 's1:所属一元购商品id',
  `open_model` enum('common','rt') DEFAULT NULL COMMENT '开奖模式,rt人还是正常',
  `category` int(11) DEFAULT NULL COMMENT '分类id',
  `mid` int(11) DEFAULT NULL COMMENT '所属大分类的id',
  `status` tinyint(11) DEFAULT '1' COMMENT 's123:当前期数状态 1未开奖 2开奖中 3已开奖',
  `deposer_type` int(11) DEFAULT NULL COMMENT 's1:夺宝类型',
  `init_times` int(11) DEFAULT '1' COMMENT 's1:初始化购买次数',
  `unit_price` decimal(10,2) DEFAULT '1.00' COMMENT 's1:单价金额 如1元',
  `min_times` int(11) DEFAULT '1' COMMENT 's1:每次最少购买次数',
  `max_times` int(5) DEFAULT '1000' COMMENT '单次最大购买限制次数',
  `sum_times` int(11) DEFAULT '0' COMMENT 's123:总次数,所需购买次数',
  `open_num` varchar(11) DEFAULT NULL COMMENT 's3:开奖时时彩号码/彩票开奖号码',
  `shuffle_name` varchar(255) DEFAULT NULL COMMENT 's1:表示存储打乱幸运数字的表名字',
  `shuffle_split_num` int(11) DEFAULT '0' COMMENT 's1:打乱的数组以多少个为间隔分割',
  `participant_num` int(11) DEFAULT '0' COMMENT 's23:参与人数,用于显示付款后参与人数,每购买一个累加1',
  `remainder` int(11) DEFAULT NULL COMMENT 's3:开奖时候求的余数',
  `luck_num` varchar(20) DEFAULT NULL COMMENT 's3:开奖计算号码',
  `luck_user` varchar(255) DEFAULT NULL COMMENT 's3:获奖用户',
  `luck_uid` int(11) DEFAULT NULL COMMENT 's3:获奖用户id',
  `luck_time` varchar(20) DEFAULT NULL COMMENT 's3:幸运用户付款时间',
  `luck_order_id` varchar(20) DEFAULT NULL COMMENT 's3:幸运订单号id',
  `lottery_issue` varchar(50) DEFAULT NULL COMMENT 's2:重庆时时彩开奖期数',
  `sum_timestamp` varchar(32) DEFAULT NULL COMMENT 's2:订单时间戳即时求和时间,触发步骤2时候记录',
  `sum_timestamp_compare` varchar(20) DEFAULT NULL COMMENT '用户每次下单购买时候累加,用于最后和sum_timestamp校验',
  `trigger_flag` enum('false','true') DEFAULT 'false' COMMENT '已经触发开奖标记,系统轮询触发检测是否开奖,触发过标记',
  `trigger_time` varchar(20) DEFAULT NULL COMMENT '1->2触发时间',
  `open_time` int(20) DEFAULT NULL COMMENT 's23:期数关闭时间/开奖时间 精确到微秒',
  `open_trigger` enum('false','true') DEFAULT 'false' COMMENT '开奖触发标记',
  `open_trigger_result` varchar(255) DEFAULT NULL COMMENT '开奖触发的返回值',
  `rt_point_uid` int(11) DEFAULT NULL COMMENT '指定用户中奖uid',
  `rt_point_type` tinyint(255) DEFAULT NULL,
  `create_time` int(11) DEFAULT NULL COMMENT 's1:创建时间',
  `nper_type` tinyint(3) DEFAULT '1' COMMENT '1:非0元抢宝2:0元抢宝',
  `odd_times` int(11) unsigned DEFAULT '0' COMMENT '奇数总需求次数',
  `even_times` int(11) unsigned DEFAULT '0' COMMENT '偶数总需求次数',
  `odd_join_num` int(11) unsigned DEFAULT '0' COMMENT '奇数参与次数',
  `even_join_num` int(11) unsigned DEFAULT '0' COMMENT '偶数参与人数',
  `sec_open` tinyint(4) DEFAULT '0' COMMENT '是否秒开(1为是,0为否)',
  `send_sms_flag` tinyint(2) DEFAULT '0' COMMENT '是否已经发送短信通知',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

