/*Table structure for table `sp_order_list` */

DROP TABLE IF EXISTS `sp_order_list`;

CREATE TABLE `sp_order_list` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `pid` int(11) DEFAULT NULL COMMENT '父级id',
  `order_id` varchar(19) DEFAULT NULL COMMENT '订单ID',
  `goods_id` int(11) DEFAULT NULL COMMENT '商品id',
  `pay_status` tinyint(3) DEFAULT '1' COMMENT '支付状态 1未支付 2支付 3成功 -2退款',
  `nper_id` int(11) DEFAULT NULL COMMENT '期数id',
  `exec_data` text COMMENT '支付接口执行JSON格式:\r\nTYPE: 1.CHARGE 2.BUY\r\nEXEC_DATA:{\r\nVALUE:值\r\n}',
  `index_start` int(11) DEFAULT NULL COMMENT '幸运数字取数坐标开始位置',
  `index_end` int(255) DEFAULT NULL COMMENT '幸运数字取数坐标结束位置',
  `dealed` enum('false','true') DEFAULT 'false' COMMENT '是否已经分配幸运数字',
  `num` int(11) DEFAULT NULL COMMENT '购买数量,同时作为取数组的往后多少位',
  `goods_name` varchar(255) DEFAULT NULL COMMENT '商品名称[用于订单列表显示,减少联合查询]',
  `bus_type` enum('recharge','buy') DEFAULT 'buy' COMMENT '业务类型:充值recharge  购买buy',
  `username` varchar(255) DEFAULT NULL COMMENT '用户名',
  `uid` int(11) DEFAULT NULL COMMENT '用户id',
  `money` decimal(11,2) DEFAULT NULL COMMENT '实付款',
  `status` tinyint(255) DEFAULT NULL COMMENT '1正常 -1删除 -2关闭',
  `promoter_pos` decimal(11,2) DEFAULT NULL COMMENT '当前推广分成比例',
  `pay_time` varchar(19) DEFAULT NULL COMMENT '支付时间,精确到毫秒的时间戳',
  `pay_time_format` varchar(255) DEFAULT NULL COMMENT '支付时间的时分秒毫秒的组合,用于累加计算开奖号码',
  `pay_sn` varchar(255) DEFAULT NULL COMMENT '支付订单号',
  `luck_status` enum('true','false') DEFAULT 'false' COMMENT '中奖标记',
  `platform` varchar(5) DEFAULT NULL COMMENT '该字段作废',
  `result` enum('false','true') DEFAULT 'false' COMMENT '购买成功失败(没用到)',
  `success_num` int(11) DEFAULT '0' COMMENT '成功数量',
  `failed_num` int(11) DEFAULT '0' COMMENT '失败数量',
  `luck_num_list` longtext,
  `create_time` varchar(20) DEFAULT NULL COMMENT '创建时间 毫秒级',
  `join_type` tinyint(3) DEFAULT '0' COMMENT '0：非0元1：奇数参与2：偶数参与',
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`) USING BTREE,
  UNIQUE KEY `order_id` (`order_id`) USING BTREE,
  KEY `uid` (`uid`),
  KEY `nper_id` (`nper_id`),
  KEY `index` (`index_start`,`index_end`)
) ENGINE=InnoDB AUTO_INCREMENT=62 DEFAULT CHARSET=utf8 COMMENT='订单表';

