/*Table structure for table `sp_origin` */

DROP TABLE IF EXISTS `sp_origin`;

CREATE TABLE `sp_origin` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL DEFAULT '' COMMENT '注册来源名字',
  `code` varchar(255) NOT NULL COMMENT '唯一标识CODE',
  `num` bigint(11) DEFAULT NULL COMMENT '通过此渠道注册的人数',
  `recharge` decimal(22,0) DEFAULT NULL COMMENT '充值总金额',
  `pay` decimal(22,0) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`,`pay`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户来源表';

/*Data for the table `sp_origin` */

/*Table structure for table `sp_packet` */

DROP TABLE IF EXISTS `sp_packet`;

CREATE TABLE `sp_packet` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `uid` int(10) NOT NULL COMMENT '用户id',
  `goods_id` int(10) NOT NULL COMMENT '商品id',
  `nper_id` int(10) NOT NULL COMMENT '期数id',
  `create_time` int(11) NOT NULL COMMENT '红包创建时间',
  `end_time` int(11) NOT NULL COMMENT '红包结束时间',
  `is_send` tinyint(1) DEFAULT '0' COMMENT '红包是否使用1使用0未使用',
  `status` tinyint(1) DEFAULT '1' COMMENT '状态1有效2删除',
  `money` decimal(10,2) DEFAULT NULL COMMENT '红包金额',
  `num` int(10) DEFAULT NULL COMMENT '红包人数',
  `goods_name` varchar(100) DEFAULT NULL COMMENT '商品名称',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

/*Data for the table `sp_packet` */

/*Table structure for table `sp_packet_record` */

DROP TABLE IF EXISTS `sp_packet_record`;

CREATE TABLE `sp_packet_record` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `wx_name` varchar(100) DEFAULT NULL COMMENT '微信昵称',
  `wx_head` varchar(150) DEFAULT NULL COMMENT '微信头像',
  `packet_id` int(10) NOT NULL COMMENT '红包id',
  `money` decimal(10,2) DEFAULT NULL COMMENT '拆取金额',
  `openid` varchar(100) DEFAULT NULL COMMENT '微信openid',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `is_get` tinyint(1) DEFAULT '0' COMMENT '是否领取1领取0未领取',
  `status` tinyint(1) DEFAULT '1' COMMENT '状态1有效2删除',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

/*Data for the table `sp_packet_record` */

/*Table structure for table `sp_phone_code` */

DROP TABLE IF EXISTS `sp_phone_code`;

CREATE TABLE `sp_phone_code` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `session` varchar(255) DEFAULT NULL COMMENT '临时session',
  `plat` varchar(20) DEFAULT NULL COMMENT '短信平台',
  `username` varchar(255) DEFAULT NULL COMMENT '用户名',
  `use_times` int(11) DEFAULT '0' COMMENT '使用次数',
  `phone_code` int(11) DEFAULT NULL COMMENT '验证码',
  `data` varchar(255) DEFAULT NULL COMMENT '返回数据',
  `phone` varchar(50) DEFAULT NULL COMMENT '手机号',
  `type` enum('fix_pass','rst_pass','reg','modify_phone') DEFAULT NULL COMMENT '''fix_pass''找回密码,''rst_pass''重置密码,''reg''注册,''modify_phone''修改手机',
  `enable` enum('true','false') DEFAULT 'true' COMMENT '1有效 2失效',
  `expire_time` int(11) DEFAULT NULL COMMENT '有效期',
  `update_time` int(11) DEFAULT NULL COMMENT '最后一次使用时间',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `session_id` (`session`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='手机验证码库';

/*Data for the table `sp_phone_code` */

