  `money` varchar(255) DEFAULT NULL COMMENT '提现金额',
  `audit_time` int(11) DEFAULT NULL,
  `create_time` int(11) DEFAULT NULL,
  `account_id` int(11) DEFAULT '0' COMMENT '账户id',
